// React frontend for AI Girlfriend + Whisper transcription with mic, avatar, premium toggle, NSFW logic, work-safe mode, TTS, Stripe stub, and mood switcher
import { useState, useRef, useEffect } from 'react';
import axios from 'axios';

export default function App() {
  const [audioFile, setAudioFile] = useState(null);
  const [transcription, setTranscription] = useState('');
  const [gfReply, setGfReply] = useState('');
  const [loading, setLoading] = useState(false);
  const [isPremium, setIsPremium] = useState(false);
  const [isWorkSafe, setIsWorkSafe] = useState(false);
  const [nsfwBlocked, setNsfwBlocked] = useState(false);
  const [mood, setMood] = useState('normal');
  const mediaRecorderRef = useRef(null);
  const [recording, setRecording] = useState(false);
  const audioChunksRef = useRef([]);

  const ttsAudioRef = useRef(null);

  const handleAudioChange = (e) => {
    setAudioFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!audioFile) return;
    setLoading(true);
    setNsfwBlocked(false);

    const formData = new FormData();
    formData.append('file', audioFile);

    try {
      const whisperRes = await axios.post('http://YOUR_WHISPER_BACKEND_IP/transcribe', formData);
      setTranscription(whisperRes.data.text);

      const replyRes = await axios.post('http://YOUR_GF_BACKEND_IP/reply', {
        prompt: whisperRes.data.text,
        premium: isPremium,
        worksafe: isWorkSafe,
        mood: mood
      });

      if (replyRes.data.nsfw && (!isPremium || isWorkSafe)) {
        setNsfwBlocked(true);
        setGfReply(isWorkSafe
          ? 'Work-safe mode is ON. Content hidden ðŸ‘”'
          : 'Unlock Premium to see what she *really* wants to say... ðŸ’‹');
      } else {
        setGfReply(replyRes.data.reply);
        playTTS(replyRes.data.reply);
      }
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const playTTS = async (text) => {
    try {
      const ttsRes = await axios.post('https://api.streamelements.com/kappa/v2/speech', {
        voice: 'Brian', // Change this to a custom voice provider if needed
        text: text
      }, { responseType: 'blob' });

      const url = URL.createObjectURL(ttsRes.data);
      const audio = new Audio(url);
      ttsAudioRef.current = audio;
      audio.play();
    } catch (err) {
      console.error('TTS failed:', err);
    }
  };

  const startRecording = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
    const mediaRecorder = new MediaRecorder(stream);
    mediaRecorderRef.current = mediaRecorder;
    audioChunksRef.current = [];

    mediaRecorder.ondataavailable = (event) => {
      audioChunksRef.current.push(event.data);
    };

    mediaRecorder.onstop = async () => {
      const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/webm' });
      const file = new File([audioBlob], 'voice.webm');
      setAudioFile(file);
    };

    mediaRecorder.start();
    setRecording(true);
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current) {
      mediaRecorderRef.current.stop();
      setRecording(false);
    }
  };

  const redirectToStripe = () => {
    window.location.href = 'https://buy.stripe.com/test_dummycheckoutlink';
  };

  return (
    <div className="min-h-screen bg-black text-white flex flex-col items-center justify-center p-4">
      <h1 className="text-2xl font-bold mb-4">AMG Voice Chat</h1>

      <div className="flex gap-4 mb-4">
        <button
          onClick={recording ? stopRecording : startRecording}
          className={`px-4 py-2 rounded-xl ${recording ? 'bg-red-600' : 'bg-green-600'} hover:opacity-90`}
        >
          {recording ? 'Stop Recording' : 'Start Recording'}
        </button>

        <input
          type="file"
          accept="audio/*"
          onChange={handleAudioChange}
          className="text-sm"
        />

        <button
          onClick={handleUpload}
          className="bg-pink-600 hover:bg-pink-700 px-4 py-2 rounded-xl"
          disabled={loading}
        >
          {loading ? 'Transcribing...' : 'Upload & Transcribe'}
        </button>
      </div>

      <div className="flex flex-col gap-2 mb-6">
        <label className="flex items-center gap-2">
          <input
            type="checkbox"
            checked={isPremium}
            onChange={(e) => setIsPremium(e.target.checked)}
          />
          Unlock Premium Mode ($4.99)
        </label>
        <label className="flex items-center gap-2">
          <input
            type="checkbox"
            checked={isWorkSafe}
            onChange={(e) => setIsWorkSafe(e.target.checked)}
          />
          Work-Safe Mode (Hide spicy content ðŸ‘”)
        </label>
        <label className="flex items-center gap-2">
          Mood:
          <select
            value={mood}
            onChange={(e) => setMood(e.target.value)}
            className="bg-gray-800 px-2 py-1 rounded"
          >
            <option value="normal">Normal</option>
            <option value="yandere">Yandere ðŸ’¢</option>
            <option value="clingy">Clingy ðŸ¥º</option>
            <option value="tsundere">Tsundere ðŸ™„</option>
            <option value="cute">Cute ðŸ˜˜</option>
          </select>
        </label>
        <button
          className="text-sm underline text-blue-400 mt-2"
          onClick={redirectToStripe}
        >
          Upgrade to Premium
        </button>
      </div>

      <div className="flex gap-6 items-start">
        <div className="w-48 h-48 bg-gray-900 rounded-xl flex items-center justify-center overflow-hidden">
          <img src="https://i.imgur.com/L0kz9bT.png" alt="GF Avatar" className="object-cover w-full h-full" />
        </div>

        <div className="flex flex-col gap-4 w-full max-w-md">
          {transcription && (
            <div className="bg-gray-800 p-4 rounded-xl">
              <h2 className="font-bold text-lg mb-2">You said:</h2>
              <p>{transcription}</p>
            </div>
          )}

          {gfReply && (
            <div className={`p-4 rounded-xl ${nsfwBlocked ? 'bg-yellow-800' : 'bg-pink-900'}`}>
              <h2 className="font-bold text-lg mb-2">GF says:</h2>
              <p>{gfReply}</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
