import { useState } from 'react';
import axios from 'axios';

export default function App() {
  const [audioFile, setAudioFile] = useState(null);
  const [transcription, setTranscription] = useState('');
  const [gfReply, setGfReply] = useState('');
  const [loading, setLoading] = useState(false);

  const handleAudioChange = (e) => {
    setAudioFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!audioFile) return;
    setLoading(true);

    const formData = new FormData();
    formData.append('file', audioFile);

    try {
      const whisperRes = await axios.post('http://YOUR_WHISPER_BACKEND_IP/transcribe', formData);
      setTranscription(whisperRes.data.text);

      const replyRes = await axios.post('http://YOUR_GF_BACKEND_IP/reply', {
        prompt: whisperRes.data.text,
      });
      setGfReply(replyRes.data.reply);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-black text-white flex flex-col items-center justify-center p-4">
      <h1 className="text-2xl font-bold mb-6">AMG Voice Chat</h1>

      <input
        type="file"
        accept="audio/*"
        onChange={handleAudioChange}
        className="mb-4"
      />

      <button
        onClick={handleUpload}
        className="bg-pink-600 hover:bg-pink-700 px-6 py-2 rounded-xl mb-6"
        disabled={loading}
      >
        {loading ? 'Transcribing...' : 'Upload & Transcribe'}
      </button>

      {transcription && (
        <div className="mb-4 w-full max-w-md bg-gray-800 p-4 rounded-xl">
          <h2 className="font-bold text-lg mb-2">You said:</h2>
          <p>{transcription}</p>
        </div>
      )}

      {gfReply && (
        <div className="w-full max-w-md bg-pink-900 p-4 rounded-xl">
          <h2 className="font-bold text-lg mb-2">GF says:</h2>
          <p>{gfReply}</p>
        </div>
      )}
    </div>
  );
}
